import { BannerComponent } from './banner-careers.component';
import { moduleMetadata } from '@storybook/angular';
import { CommonModule } from '@angular/common'; // Import CommonModule
export default {
    title: 'Components/Banner Careers',
    component: BannerComponent,
    decorators: [
        moduleMetadata({
            imports: [CommonModule], // Add CommonModule to Storybook metadata
        }),
    ],
    argTypes: {
        showBanner: { control: 'boolean' }, // Add control to toggle the showBanner input
    },
};
const Template = (args) => ({
    props: args,
});
export const Visible = Template.bind({});
Visible.args = {
    showBanner: true, // Initially show the banner
};
export const Hidden = Template.bind({});
Hidden.args = {
    showBanner: false, // Initially hide the banner
};
export const ToggleVisibility = Template.bind({});
ToggleVisibility.args = {
    showBanner: true,
};
ToggleVisibility.parameters = {
    actions: {
        handles: ['click button'], // You can handle the button click for interaction
    },
};
