module.exports = "<!-- BUTTON: An Atom - iDeparture Design System v.3.0.1 -->\n<!-- <button class=\"w-full text-left text-accent-500 bg-transparent hover:text-accent-300 hover:bg-transparent text-sm\">\n    Text Only Button\n</button> -->\n\n<!-- BUTTON: An Atom - iDeparture Design System v.3.0.1 -->\n<button \n  class=\"w-full text-left text-accent-500 bg-transparent hover:text-accent-300 hover:bg-transparent text-sm\"\n  [ngClass]=\"{\n    'hover:text-accent-300': styleType === 'text-only' || styleType === 'outline',\n    'bg-primary text-neutral': styleType === 'filled',\n    'border border-accent text-accent-500 hover:bg-accent-100': styleType === 'outline',\n    'flex items-center justify-start': icon\n  }\"\n  [ngStyle]=\"{'background-color': backgroundColor}\"\n  (click)=\"buttonClick.emit()\"\n>\n  <!-- Icon rendering if provided -->\n  <ng-container *ngIf=\"icon\">\n    <svg class=\"mr-2 h-5 w-5\" [attr.fill]=\"styleType === 'filled' ? '#FFFFFF' : 'currentColor'\">\n      <use [attr.href]=\"'#' + icon\"></use>\n    </svg>\n  </ng-container>\n\n  <!-- Button Text -->\n  <span>{{ text }}</span>\n</button>\n\n\n";

